import React, { useEffect } from 'react';
import styles from './ChangeModal.module.scss'
import {Button, FormControl, InputLabel, MenuItem, Select, TextField} from '@mui/material';
import {addCash} from '../../api/userApi';
import {useForm} from 'react-hook-form';
import {useCurrency} from '../../hooks/currency.hook';

const Index = ({isModal, setIsModal, getUserData, isName}) => {
  const {currency, selectedCurrency, setSelectedCurrency, getAvailableCurrency} = useCurrency();

  const handleSelectChange = (event) => {
    setSelectedCurrency(event.target.value);
  }

  const {
    register,
    handleSubmit,
    // setError,
    formState: {errors, isValid},
  } = useForm({
    defaultValues: {amount: ''},
    mode: 'onBlur',
  });

  const onSubmit = async (values) => {
    await addCash({currency: selectedCurrency, ...values})
      .then(data => {
        console.log(data);
        getUserData().then();
        setIsModal(false)
      })
      .catch(err => console.log(err));
  };

  useEffect(() => {
    getAvailableCurrency();
  }, [getAvailableCurrency]);

  return (
    <div style={{display: isModal ? 'flex' : 'none'}} className={styles.background}>
      <form onSubmit={handleSubmit(onSubmit)} className={styles.inner}>
        <div className={styles.row}>
          <TextField type={'number'} fullWidth label="amount" variant="outlined"
                     error={!!errors.amount?.message}
                     helperText={errors.amount?.message}
                     {...register('amount', {required: "Вкажіть кілкість"})}
          />
          <FormControl fullWidth>
            <InputLabel>Currency</InputLabel>
            <Select
              label="currency"
              value={selectedCurrency}
              onChange={handleSelectChange}
            >
              {currency?.map((elem, index) => <MenuItem key={index} value={elem}>{elem}</MenuItem>)}
            </Select>
          </FormControl>
          {isName && <TextField fullWidth label="name" variant="outlined" />}
        </div>
        <div className={styles.row}>
          <Button type={'submit'} variant={'contained'} color={'primary'} disabled={!isValid}>Зберегти</Button>
          <Button variant={'contained'} color={'error'} onClick={() => setIsModal(false)}>Скасувати</Button>
        </div>
      </form>
    </div>
  );
};

export default Index;
