import React, {useCallback, useEffect, useState} from 'react';
import {CircularProgress, Container} from '@mui/material';
import {getMe} from './api/userApi';
import Header from './components/Header';
import Home from './pages/Home';
import Auth from './pages/Auth';
import {Routes, Route, Navigate, useNavigate, BrowserRouter} from 'react-router-dom';
import {useAuth} from './hooks/auth.hook';
import {AuthContext} from "./context/AuthContext";

const App = () => {
  const navigate = useNavigate();
  const {token, login, logout, userId, ready} = useAuth();
  const isAuthenticated = !!token;
  const [user, setUser] = useState({});

  const getUserData = useCallback( async () => {
    const user = await getMe();
    setUser(user);
  }, []);
  console.log('auth' + isAuthenticated);
  console.log('ready' + ready);

  useEffect(() => {
    if (ready) {
      if (isAuthenticated) {
        getUserData().then();
      } else {

      }
    }
  }, [getUserData, isAuthenticated, ready])

  if (!ready) {
    return <CircularProgress />
  }

  return (
    <AuthContext.Provider value={{
      login, logout, userId, isAuthenticated
    }}>
      <BrowserRouter>
        <Container maxWidth={'md'}>
          <Header/>
          <Routes>
            {isAuthenticated ? <Route path={'/'} element={<Home/>}/> : <Route path={'/auth'} element={<Auth/>}/>}
          </Routes>
        </Container>
      </BrowserRouter>
    </AuthContext.Provider>
  );
};

export default App;
