import React, {useCallback, useState} from 'react';
import {Paper} from '@mui/material';
import styles from './Cards.module.scss'
import AddCard from '../AddCard';
import CardsInfo from '../CardsInfo';
import ChangeModal from '../ChangeModal';
import {getCurrency} from '../../api/userApi';

const Index = ({getUserData}) => {
  const [currency, setCurrency] = useState(null);
  const [selectedCurrency, setSelectedCurrency] = useState('');
  const [page, setPage] = useState('CardsInfo');
  const [isModal, setIsModal] = useState(false);

  const getAvailableCurrency = useCallback(async () => {
    await getCurrency().then(res => {
      setCurrency(res);
      setSelectedCurrency(res[0]);
    });
  }, [setCurrency]);

  return (
    <Paper className={styles.container}>
      {page === 'CardsInfo' ? <CardsInfo setIsModal={setIsModal} setPage={setPage}/> : <AddCard getAvailableCurrency={getAvailableCurrency} setSelectedCurrency={setSelectedCurrency} selectedCurrency={selectedCurrency} currency={currency} setCurrency={setCurrency} setPage={setPage} getUserData={getUserData}/>}
      <ChangeModal currency={currency} setCurrency={setCurrency} getUserData={getUserData} isModal={isModal} setIsModal={setIsModal}/>
    </Paper>
  );
};

export default Index;
