import React from 'react';
import styles from './ChangeModal.module.scss'
import {Button, Paper, TextField} from '@mui/material';

const Index = ({isModal, setIsModal}) => {
  return (
    <div style={{display: isModal ? 'block' : 'none'}} className={styles.background}>
      <Paper className={styles.inner}>
        <div className={styles.row}>
          <TextField fullWidth label="amount" variant="outlined" />
          <TextField fullWidth label="currency" variant="outlined" />
          <TextField fullWidth label="name" variant="outlined" />
        </div>
        <div className={styles.row}>
          <Button variant={'contained'} color={'primary'}>Зберегти</Button>
          <Button variant={'contained'} color={'error'} onClick={() => setIsModal(false)}>Скасувати</Button>
        </div>
      </Paper>
    </div>
  );
};

export default Index;
