import React, {useCallback, useEffect, useState} from 'react';
import {Container} from '@mui/material';
import {getMe} from './api/userApi';
import {userId} from './consts';
import Header from './components/Header';
import Home from './pages/Home';
import Auth from './pages/Auth';
import {Routes, Route, Navigate, useNavigate} from 'react-router-dom';

const App = () => {
  const [isAuth, setIsAuth] = useState(false);
  const [isReady, setIsReady] = useState(false);
  const [user, setUser] = useState({});

  const navigate = useNavigate();

  const checkAuth = useCallback(() => {
    const token = window.localStorage.getItem('token');
    if (token) {
      setIsAuth(true);
      setIsReady(true);
      console.log('auth ok')
    }
    console.log('auth failed');
    navigate("/auth", { replace: true });
  }, [navigate])

  const getUser = useCallback( async () => {
    console.log('get user');
    const user = await getMe(userId);
    setUser(user);
  }, []);

  useEffect(() => {
    checkAuth();
    if (isAuth && isReady) {
      getUser().then();
    }
  }, [getUser, isAuth, isReady, checkAuth]);

  return (
    <>
      <Container maxWidth={'md'}>
        <Header/>
        <Routes>
          {isAuth ? <Route path={'/'} element={<Home/>}/>: <Route to={'/auth'} element={<Auth/>}/>}
          {isAuth ? <Route path="*" element={<Navigate to="/" replace />} /> : <Route path="*" element={<Navigate to="/auth" replace />} />}
        </Routes>
      </Container>
    </>
  );
};

export default App;
