import React, {useCallback, useContext, useEffect, useState} from 'react';
import {Avatar, Button, Paper} from '@mui/material';
import styles from './MainInfo.module.scss'
import List from '../List';
import {getMe} from '../../api/userApi';
import {AuthContext} from '../../context/AuthContext';

const Index = () => {
  const [userData, setUserData] = useState(null);

  const {ready, isAuthenticated, logout} = useContext(AuthContext);


  const getUserData = useCallback( async () => {
    const user = await getMe();
    console.log(user);
    setUserData(user);
  }, []);

  useEffect(() => {
    if (ready) {
      if (isAuthenticated && window.localStorage.getItem('token')) {
        getUserData().then();
      }
    }
  }, [ready, isAuthenticated, getUserData])

  return (
    <Paper className={styles.container}>
      <div className={styles.row}>
        <div className={styles.user}><Avatar alt={userData?.name} src={`http://localhost:5000${userData?.avatarUrl}`} /><span className={styles.userName}>{userData?.name}</span></div>
        {isAuthenticated && <Button onClick={logout} >Выйти</Button>}
      </div>
      <div className={styles.block}>
        <p>Баланс</p>
        <List>
          {userData?.balance?.map((elem, index) =>
            <div>
              <span>{elem?.amount}</span> <span>{elem?.currency}</span>
            </div>)}
        </List>
      </div>
      <div className={styles.block}>
        <p>Готівка</p>
        <List>
          {userData?.cash.map((elem, index) =>
            - <div key={index} className={styles.line}>
              <div>
                <span>{elem?.amount}</span> <span>{elem?.currency}</span>
              </div>
              <Button variant={'contained'}>Редагувати</Button>
            </div>)}
        </List>
      </div>
      <div className={styles.block}>
        <p>Мої картки</p>
        <List>
          <div className={styles.line}>- <div><span>Моно </span> <span>287.75</span> <span>UAH</span></div> <Button variant={'contained'}>Редагувати</Button></div>
          <div className={styles.line}>- <div><span>Приват </span> <span>500</span> <span>USD</span></div> <Button variant={'contained'}>Редагувати</Button></div>
        </List>
      </div>
    </Paper>
  );
};

export default Index;
