import React, {useCallback, useContext, useEffect, useState} from 'react';
import {Avatar, Button, Paper} from '@mui/material';
import styles from './MainInfo.module.scss'
import List from '../List';
import {getBalance, getCash, getMe} from '../../api/userApi';
import {AuthContext} from '../../context/AuthContext';

const Index = () => {
  const [userData, setUserData] = useState(null);
  const [userBalance, setUserBalance] = useState(null);
  const [userCash, setUserCash] = useState(null);

  const {ready, isAuthenticated} = useContext(AuthContext);


  const getUserData = useCallback( async () => {
    const user = await getMe();
    console.log(user)
    setUserData(user);
  }, []);

  const getUserBalance = useCallback( async () => {
    const balance = await getBalance();
    setUserBalance(balance);
  }, []);

  const getUserCash = useCallback( async () => {
    const balance = await getCash();
    setUserBalance(balance);
  }, []);

  useEffect(() => {
    console.log(ready)
    if (ready) {
      const localToken = window.localStorage.getItem('token');
      if (isAuthenticated&& localToken) {
        getUserData().then();
        getUserBalance().then();
        getUserCash().then();
      }
    }
  }, [getUserData, ready, isAuthenticated, getUserBalance])

  return (
    <Paper className={styles.container}>
      <div className={styles.row}>
        <Avatar alt={userData?.name} src={`http://localhost:5000${userData?.avatarUrl}`} /><span className={styles.userName}>{userData?.name}</span>
      </div>
      <div className={styles.block}>
        <p>Баланс</p>
        <List>
          <li><span>{userBalance?.amount}</span> <span>{userBalance?.currency}</span></li>
          <li><span>500</span> <span>USD</span></li>
        </List>
      </div>
      <div className={styles.block}>
        <p>Готівка</p>
        <List>
          <li><div className={styles.line}><div><span>1000</span> <span>UAH</span></div> <Button variant={'contained'}>Редагувати</Button></div></li>
        </List>
      </div>
      <div className={styles.block}>
        <p>Мої картки</p>
        <List>
          <li><div className={styles.line}><div><span>Моно </span> <span>287.75</span> <span>UAH</span></div> <Button variant={'contained'}>Редагувати</Button></div></li>
          <li><div className={styles.line}><div><span>Приват </span> <span>500</span> <span>USD</span></div> <Button variant={'contained'}>Редагувати</Button></div></li>
        </List>
      </div>
    </Paper>
  );
};

export default Index;
