import React, {useCallback, useContext, useEffect, useState} from 'react';
import {Grid} from '@mui/material';
import Main from '../../components/MainInfo';
import Cards from '../../components/Cards';
import {getMe, updateCard} from '../../api/mainApi';
import {AuthContext} from '../../context/AuthContext';

const Index = () => {
  const {ready, isAuthenticated} = useContext(AuthContext);
  const [userData, setUserData] = useState(null);
  const [changeItemData, setChangeItemData] = useState({
    id: null,
    amount: null,
    name: null,
    currency: null
  });
  console.log(userData);

  const getUserData = useCallback( async () => {
    const user = await getMe();
    setUserData(user);
  }, []);

  const changeItemHandler = async (id, newAmount, name) => {
    await updateCard(id, newAmount, name).then(res => {
      console.log(res);
      getUserData();
    })
  }

  useEffect(() => {
    console.log(changeItemData);
    console.log('effect')
  }, [changeItemData])


  useEffect(() => {
    if (ready) {
      if (isAuthenticated && window.localStorage.getItem('token')) {
        getUserData().then();
      }
    }
  }, [ready, isAuthenticated, getUserData])

  return (
    <Grid container spacing={0}>
      <Grid item xs={5} display={'flex'} justifyContent={'center'}>
        <Main itemData={setChangeItemData} getUserData={getUserData} userData={userData}/>
      </Grid>
      <Grid item xs={7} display={'flex'} justifyContent={'center'}>
        <Cards changeItemData={changeItemData} cards={userData?.cards} getUserData={getUserData}/>
      </Grid>
    </Grid>
  );
};

export default Index;
