import React from 'react';
import styles from './AddCard.module.scss';
import {Button, TextField, Typography} from '@mui/material';
import {useForm} from 'react-hook-form';

const Index = () => {
  const {
    register,
    handleSubmit,
    // setError,
    formState: {errors, isValid},
  } = useForm({
    defaultValues: {number: '', expDate: '', cvv: '', holder: '', amount: '', currency: ''},
    mode: 'onBlur',
  });
  return (
    <form>
      <Typography variant="h5" className={styles.title} >
        Додавання картки
      </Typography>
      <div className={styles.row}>
        <TextField fullWidth label="Card number" variant="outlined"
                   error={!!errors.number?.message}
                   helperText={errors.number?.message}
                   {...register('number', {required: "Вкажіть номер картки", minLength: {
                       value: 16,
                       message: "Minimum 16 symbols",
                     },
                     maxLength: {
                       value: 16,
                       message: "Maximum 16 symbols",
                     },
                     valueAsNumber: true,
                     pattern:{
                       value: /^(0|[1-9]\d*)(\.\d+)?$/
                     },

                   })}
        />
      </div>
      <div className={styles.row}>
        <TextField label="exp date" required variant="outlined"
                   error={!!errors.expDate?.message}
                   helperText={errors.expDate?.message}
                   {...register('expDate', {required: 'Вкажіть термін дії'})}
        />
        <TextField type={'password'} label="CVV" required variant="outlined"
                   error={!!errors.cvv?.message}
                   helperText={errors.cvv?.message}
                   {...register('cvv', {required: 'Вкажіть CVV'})}
        />
      </div>
      <div className={styles.row}>
        <TextField fullWidth label="Card holder" variant="outlined"
                   error={!!errors.holder?.message}
                   helperText={errors.holder?.message}
                   {...register('holder', {required: "Вкажіть ім'я власника"})}
        />
      </div>
      <div className={styles.row}>
        <TextField label="amount" required variant="outlined"
                   error={!!errors.amount?.message}
                   helperText={errors.amount?.message}
                   {...register('amount', {required: 'Вкажіть баланс'})}
        />
        <TextField label="currency" required variant="outlined"
                   error={!!errors.currency?.message}
                   helperText={errors.currency?.message}
                   {...register('currency', {required: 'Вкажіть валюту'})}
        />
      </div>
      <div className={styles.row}>
        <Button variant={'contained'} type={'submit'} color={'primary'} >Додати картку</Button>
        <Button variant={'contained'} color={'error'}>Скасувати</Button>
      </div>
    </form>
  );
};

export default Index;
