import React from 'react';
import {Avatar, Button, Paper} from '@mui/material';
import styles from './MainInfo.module.scss'
import List from '../List';

const Index = ({user}) => {
  // console.log(user)
  return (
    <Paper className={styles.container}>
      <div className={styles.row}>
        <Avatar alt={user?.name} src={`http://localhost:5000${user?.avatarUrl}`} /><span className={styles.userName}>{user?.name}</span>
      </div>
      <div className={styles.block}>
        <p>Баланс</p>
        <List>
          <li><span>1287.75</span> <span>UAH</span></li>
          <li><span>500</span> <span>USD</span></li>
        </List>
      </div>
      <div className={styles.block}>
        <p>Готівка</p>
        <List>
          <li><div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}><div><span>1000</span> <span>UAH</span></div> <Button variant={'contained'}>Редагувати</Button></div></li>
        </List>
      </div>
      <div className={styles.block}>
        <p>Мої картки</p>
        <List>
          <li><div><span>Моно </span> <span>287.75</span> <span>UAH</span></div> <Button variant={'contained'}>Редагувати</Button></li>
          <li><div><span>Приват </span> <span>500</span> <span>USD</span></div> <Button variant={'contained'}>Редагувати</Button></li>
        </List>
      </div>
    </Paper>
  );
};

export default Index;
