import React, {useState} from 'react';
import {Paper} from '@mui/material';
import styles from './Cards.module.scss'
import AddCard from '../AddCard';
import CardsInfo from '../CardsInfo';
import ChangeModal from '../ChangeModal';
import {removeCard} from '../../api/mainApi';

const Index = ({getUserData, cards}) => {
  const [page, setPage] = useState('CardsInfo');
  const [isModal, setIsModal] = useState(false);

  const handleRemoveCard = async (id) => {
    await removeCard(id).then(res => {
      console.log(res);
      getUserData();
    }).catch(err => console.log(err));
  }

  return (
    <Paper className={styles.container}>
      {page === 'CardsInfo' ? <CardsInfo cards={cards} setIsModal={setIsModal} setPage={setPage}/> : <AddCard setPage={setPage} getUserData={getUserData}/>}
      <ChangeModal getUserData={getUserData} isModal={isModal} setIsModal={setIsModal}/>
    </Paper>
  );
};

export default Index;
