import React, {useCallback, useEffect, useState} from 'react';
import styles from './AddCard.module.scss';
import {Button, FormControl, InputLabel, MenuItem, Select, TextField, Typography} from '@mui/material';
import {useForm} from 'react-hook-form';
import {getCurrency} from '../../api/userApi';

const Index = () => {

  const [currency, setCurrency] = useState(null);

  const [selectedCurrency, setSelectedCurrency] = useState('');

  const getAvailableCurrency = useCallback(async () => {
    await getCurrency().then(res => {
      setCurrency(res);
      setSelectedCurrency(res[0]);
    });
  }, []);

  function luhnAlgorithm(value) {
    value = value.replace(/\D/g, '');

    let nCheck = 0;
    let bEven = false;

    for (let n = value.length - 1; n >= 0; n--) {
      let nDigit = parseInt(value.charAt(n), 10);

      if (bEven && (nDigit *= 2) > 9) {
        nDigit -= 9;
      }

      nCheck += nDigit;
      bEven = !bEven;
    }

    return (nCheck % 10) === 0;
  }

  console.log(luhnAlgorithm('12341234123444'));

  const handleSelectChange = (event) => {
    setSelectedCurrency(event.target.value);
  }
  const {
    register,
    handleSubmit,
    // setError,
    formState: {errors, isValid},
  } = useForm({
    defaultValues: {number: '', expDate: '', cvv: '', holder: '', amount: ''},
    mode: 'onBlur',
  });

  const maxLength = (e, maxLength) => {
    e.target.value = Math.max(0, parseInt(e.target.value) ).toString().slice(0, maxLength)
  }

  useEffect(() => {
    getAvailableCurrency();
  }, [getAvailableCurrency]);

  return (
    <form>
      <Typography variant="h5" className={styles.title} >
        Додавання картки
      </Typography>
      <div className={styles.row}>
        <TextField fullWidth type={'number'} inputProps={{ maxLength: 12}} label="Card number" variant="outlined"
                   onInput = {(e) => maxLength(e, 16)}
                   error={!!errors.number?.message}
                   helperText={errors.number?.message}
                   {...register('number', {required: "Вкажіть номер картки"})}
        />
      </div>
      <div className={styles.row}>
        <TextField fullWidth type={'number'} label="exp date" variant="outlined"
                   onInput = {(e) => maxLength(e, 4)}
                   error={!!errors.expDate?.message}
                   helperText={errors.expDate?.message}
                   {...register('expDate', {required: 'Вкажіть термін дії'})}
        />
        <TextField fullWidth type={'number'} label="CVV" variant="outlined"
                   onInput = {(e) => maxLength(e, 3)}
                   error={!!errors.cvv?.message}
                   helperText={errors.cvv?.message}
                   {...register('cvv', {required: 'Вкажіть CVV'})}
        />
      </div>
      <div className={styles.row}>
        <TextField fullWidth label="Card holder" variant="outlined"
                   error={!!errors.holder?.message}
                   helperText={errors.holder?.message}
                   {...register('holder')}
        />
      </div>
      <div className={styles.row}>
        <TextField fullWidth type={'number'} label="amount" variant="outlined"
                   error={!!errors.amount?.message}
                   helperText={errors.amount?.message}
                   {...register('amount', {required: 'Вкажіть баланс'})}
        />
        <FormControl fullWidth>
          <InputLabel>Currency</InputLabel>
          <Select
            label="currency"
            value={selectedCurrency}
            onChange={handleSelectChange}
          >
            {currency?.map(elem => <MenuItem key={elem} value={elem}>{elem}</MenuItem>)}
          </Select>
        </FormControl>
      </div>
      <div className={styles.row}>
        <Button variant={'contained'} type={'submit'} color={'primary'} disabled={!isValid}>Додати картку</Button>
        <Button variant={'contained'} color={'error'}>Скасувати</Button>
      </div>
    </form>
  );
};

export default Index;
