import React, {useCallback, useEffect, useState} from 'react';
import styles from './ChangeModal.module.scss'
import {Button, FormControl, InputLabel, MenuItem, Paper, Select, TextField} from '@mui/material';
import {getCurrency} from '../../api/userApi';

const Index = ({isModal, setIsModal, isName}) => {

  const [currency, setCurrency] = useState(null);

  const [selectedCurrency, setSelectedCurrency] = useState('');

  const getAvailableCurrency = useCallback(async () => {
    await getCurrency().then(res => {
      setCurrency(res);
      setSelectedCurrency(res[0]);
    });
  }, []);

  const handleSelectChange = (event) => {
    setSelectedCurrency(event.target.value);
  }

  useEffect(() => {
    getAvailableCurrency();
  }, [getAvailableCurrency]);

  return (
    <div style={{display: isModal ? 'flex' : 'none'}} className={styles.background}>
      <Paper className={styles.inner}>
        <div className={styles.row}>
          <TextField fullWidth label="amount" variant="outlined" />
          <FormControl fullWidth>
            <InputLabel>Currency</InputLabel>
            <Select
              label="currency"
              value={selectedCurrency}
              onChange={handleSelectChange}
            >
              {currency?.map((elem, index) => <MenuItem key={index} value={elem}>{elem}</MenuItem>)}
            </Select>
          </FormControl>
          {isName && <TextField fullWidth label="name" variant="outlined" />}
        </div>
        <div className={styles.row}>
          <Button variant={'contained'} color={'primary'}>Зберегти</Button>
          <Button variant={'contained'} color={'error'} onClick={() => setIsModal(false)}>Скасувати</Button>
        </div>
      </Paper>
    </div>
  );
};

export default Index;
